
public class parseXxxmethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//through the parseXXX() method we can convert String type to any primitive type.
		int i=Integer.parseInt("10");
		System.out.println(i);
		boolean b=Boolean.parseBoolean("TRUE");
		System.out.println(b);
		float f=Float.parseFloat("5.0");
		System.out.println(f);
		byte b1=Byte.parseByte("11");
		System.out.println(b1);
		long l=Long.parseLong("45466464");
		System.out.println(l);
		double d=Double.parseDouble("783.00");
		System.out.println(d);
	}

}
